(256, 1, '2020-12-01 23:22:52', '2020-12-01 22:22:52', '/**\n * logo\n **/\n\n.navbar-inverse .navbar-brand p.site-description, .navbar-inverse .navbar-brand .site-title {\n    margin-bottom: 0;\n    color: #00adb5;\n    font-family: \"Montserrat\", Helvetica, sans-serif;\n    font-size: 16px;\n    font-weight: bold;\n    line-height: normal;\n}\n\n.navbar-inverse .navbar-brand .site-title a {\n    color: #00adb5;\n}\n\n\n/**\n * menu despegable de la tienda\n **/\n\n.navbar-inverse .navbar-nav ul.sub-menu li a:hover {\n    color: #00adb5;\n}\n\n.navbar-inverse .navbar-nav ul.sub-menu li a:active {\n    color: #00adb5;\n}\n\n/**\n * boton despegable movil\n **/\n\n.navbar-inverse .navbar-toggle:hover, .navbar-inverse .navbar-toggle:focus {\n    opacity: 1;\n    background-color: #00adb5;\n    box-shadow: none;\n    filter: alpha(opacity=100);\n}\n\n.navbar-toggle.active {\n    background-color: #00adb5!important;\n}\n\n/**\n * boton despegable tienda movil\n **/\n\n@media (max-width: 100px)\n.dropdownmenu {\n    display: block;\n    position: absolute;\n    z-index: 9;\n    top: 5px;\n    right: 0;\n    width: 25px;\n    height: 25px;\n    margin: 0;\n    padding: 0;\n    border-radius: 3px;\n    background: url(images/menu-icon.png) center center no-repeat #00adb5;\n}\n\n/**\n * enlaces del menu\n **/\n\n.navbar-inverse .navbar-nav > li > a:hover {\n    padding: 0;\n    color: #00adb5;\n    line-height: 35px;\n}\n\n.navbar-inverse .navbar-nav > li > a:active {\n    padding: 0;\n    color: #00adb5;\n    line-height: 35px;\n}\n\n\n/**border-bottom de los enlaces activos del menu\n*/\n\nul.nav > li.current > a:before, .page ul.nav > li.current-menu-item > a:before, .single-post ul.nav > li.current-menu-item > a:before, .archive ul.nav > li.current-menu-item > a:before {\n    display: block;\n    position: absolute;\n    z-index: 1;\n    bottom: 0;\n    left: 12.5%;\n    width: 75%;\n    height: 2px;\n    margin: auto;\n    background: #00adb5;\n    content: \"\";\n}\n